import React from 'react'
import CategoryCard from '../components/CategoryCard'
import Helmet from "../components/Helmet";
import Topic from '../components/Topic';
import Category from './Category';
import { useSelector, useDispatch } from "react-redux";
import PostSlice, {
  getAllBlogsByUserIDAsync,
} from "../features/post/PostSlice";
import { useState, useEffect } from "react";


function Home() {
  const allPosts = useSelector((state) => state.post);
  //const topicIDList = allPosts.find(post =>post.User.id===userID)
  const [userID, setUserID] = useState(2);
  console.log(allPosts.Use);
  console.log(topicIDList);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getAllBlogsByUserIDAsync(userID));
  }, [dispatch]);
  const allTopicIDElements = allPosts.map((post) => {
    return (
      <Topic topicName={topicIDList.topicName}
            id ={topicIDList.id}
      />
    );
  });
  return (
    <div>
      <Helmet title="Trang chủ">Trang chủ</Helmet>
      {allTopicIDElements}
    </div>
  );
}

export default Home
