import React from 'react';
import { useState } from 'react';
import { useContext } from 'react';
import Avatar from 'react-avatar-edit';

const Settings = (props) => {
    var [src, setSrc] = useState();
    var [editting, setEditting] = useState(false);

    const onEditClick = () => {};

    const onImageLoad = (e) => {
        // setWidth()
        console.log(e);
    };

    return (
        <div className="settings">
            <div className="settings__left">
                <div className="settings__left__pfp">
                    {editting === true ? (
                        <Avatar width={350} height={450} src={src}></Avatar>
                    ) : props.blogger.profilepic === undefined ? (
                        <img
                            src={
                                require('../')
                            }
                            alt=""
                        />
                    ) : (
                        <img
                            src={
                                'data:image/jpg;base64,' +
                                props.blogger.profilepic
                            }
                            alt=""
                        />
                    )}
                </div>
                <div className="settings__left__edit" onClick={onEditClick}>
                    Change profile pic
                </div>
            </div>
            <div className="settings__right"></div>
        </div>
    );
};

export default Settings;
