import React from "react";
import { useNavigate } from "react-router-dom";

function DropdownMenu(props) {
	function DropdownItem(props) {
		return (
			<div className="dropdown__item">
				<span className="dropdown__item__icon-button">{props.leftIcon}</span>
				{props.children}
				<span className="dropdown__item__icon-right">{props.rightIcon}</span>
			</div>
		);
	}

	const navigate = useNavigate();
	handleClick = (e) => {
		e.preventDefault();
		console.log(e.target.props);
		// navigate(`/topic/${e.target.children}`);
	};

	return (
		<div className="dropdown">
			{props.topic.map((topic, index) => {
				return (
					<DropdownItem key={index} onClick={this.dropdownItemClick}>
						{topic.topicName}
					</DropdownItem>
				);
			})}
		</div>
	);
}

export default DropdownMenu;
