import React from "react";
import CategoryCard from "../components/CategoryCard";
import Helmet from "../components/Helmet";
import Topic from "../components/Topic";
import Category from "./Category";
import { useSelector, useDispatch } from "react-redux";
import PostSlice, {
  getAllTopicsByUserIDAsync,
} from "../features/";
import { useState, useEffect } from "react";

function Home() {
  const allPosts = useSelector((state) => state.post);
  const topicIDList = allPosts.map((post) => post.Topic);
  const [userID, setUserID] = useState(2);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getAllTopicsByUserIDAsync(userID));
  }, [dispatch]);
  const allTopicIDElements = allPosts.map((post) => {
    return <Topic topicName={topicIDList.topicName} id={1} />;
  });
  return (
    <div>
      <Helmet title="Trang chủ">Trang chủ</Helmet>
      {allTopicIDElements}
    </div>
  );
}

export default Home;
