import React from 'react';
import { useRef } from 'react';
import { useState } from 'react';
import { useContext } from 'react';
import Avatar from 'react-avatar-edit';

const Settings = (props) => {
    var [src, setSrc] = useState();
    var [editting, setEditting] = useState(false);

    const editRef = useRef()

    const onEditClick = () => {
        setEditting(true);
        editRef.current.classList.add('editting')
        editRef.current.innerHTML = '&#10003';
    };

    const onImageLoad = (e) => {
        // setWidth()
        console.log(e);
    };

    return (
        <div className="Settings">
            <div className="Settings__left">
                <div className="Settings__left__pfp">
                    {editting === true ? (
                        <Avatar width={350} height={450} src={src}></Avatar>
                    ) : props.blogger.profilepic === undefined ? (
                        <div className="Settings__left__pfp__img-container">
                            <img
                                src={require('../assets/image/user/alt.png')}
                                alt=""
                            />
                        </div>
                    ) : (
                        <div className="Settings__left__pfp__img-container">
                            <img
                                src={
                                    'data:image/jpg;base64,' +
                                    props.blogger.profilepic
                                }
                                alt=""
                            />
                        </div>
                    )}
                </div>
                <div className="Settings__left__edit" onClick={onEditClick} ref={editRef}>
                    Đổi ảnh
                </div>
            </div>
            <div className="Settings__right"></div>
        </div>
    );
};

export default Settings;
