import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import Axios from "axios";

export const authenticateAccountAsync = createAsyncThunk(
	"accounts/authenticateAccountAsync",
	async (username, password) => {
		const res = await Axios.get(`http://localhost:5000/auth}`);
		const tasks = res.data;
		return { tasks };
	}
);

const accountSlice = createSlice({
	name: "account",
	initialState: [],
	reducers: {
		login:  (state, action) => {
      console.log('action payload:' + action.payload)
			const res =  Axios.get("http://localhost:5000/auth}", {
				params: {
					username: action.payload.username,
					password: action.payload.password,
				},
			});
			if (res) state.account = action.payload;
			else state.account = null;
		},
		logout: (state) => {
			state.account = null;
		},
	},
	extraReducers: {
		[authenticateAccountAsync.fulfilled]: (state, action) => {
			console.log("account authentication successfully");
			return action.payload.tasks;
		},
	},
});
export const { login, logout } = accountSlice.actions;
export default accountSlice.reducer;
