import React from "react";
import { useRef } from "react";

import Helmet from "../components/Helmet";

const NewBlog = () => {
	const textareaRef = useRef();

	return (
		<Helmet title="Tạo blog mới">
			<div className="new-blog">
				<form className="new-blog__form">
					<input type="text" className="new-blog__form__topic" />
					<input
						type="text"
						className="new-blog__form__title"
						placeholder="Tiêu đề"
					/>
					<input
						type="text"
						className="new-blog__form__place"
						placeholder="Địa điểm"
					/>
					<textarea
						type="text"
						className="new-blog__form__body"
						placeholder="Nội dung"
						ref={textareaRef}
						onKeyUp={(e)=>{
							let height = e.target.scrollHeight;
							
						}}
						></textarea>
					<input
						type="text"
						className="new-blog__form__signature"
						placeholder="Chữ ký"
					/>
					<button
						className="new-blog__form__create-button"
						type="submit"></button>
				</form>
			</div>
		</Helmet>
	);
};

export default NewBlog;
