import React from "react";

export default function BlogCard(props) {
	var img = new Image();
	var imgShape;
	img.onload = function () {
		if (this.width > this.height) imgShape = 'landscape';
		else landscape = false;
	};
	img.src = props.urlImage;

	return (
		<div className="body" id={props.id}>
			<div className="body-image">
				<div className="body-image-container">
					<img src={img} alt="" />
				</div>
			</div>
			<div className="body-content">
				<h3>{props.title}</h3>
				<p>{props.content}</p>
			</div>
		</div>
	);
}
