import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { login } from "../features/user/userSlice";

import loginImage01 from "../assets/image/login/login-image-01.png";
import logo from "../assets/image/login/login-logo.png";

const Login = () => {
	const [username, setUsername] = useState("");
	const [password, setPassword] = useState("");

	const dispatch = useDispatch();

	const handleLogin = (e) => {
		e.preventDefault();

		dispatch(
			login({
				username: username,
				password: password,
				loggedIn: true,
			})
		);
	};

	return (
		<div className="login">
			<div className="login__image">
				<img src={loginImage01} alt="" />
			</div>
			<form className="login__form" onSubmit={handleLogin}>
				<div className="login__logo">
					<img src={logo} alt="" />
				</div>
				<div className="login__form__item">
					<div className="login__form__item__title">tài khoản</div>
					<input
						type="text"
						name="username"
						id="username"
						onChange={(e) => setUsername(e.target.value)}
					/>
				</div>
				<div className="login__form__item">
					<div className="login__form__item__title">mật khẩu</div>
					<input
						type="password"
						name="password"
						id="password"
						onChange={(e) => setPassword(e.target.value)}
					/>
				</div>
				<button type="submit" className="login__form__login-button">
					đăng nhập
				</button>
			</form>
		</div>
	);
};

export default Login;
