import React from "react";
import CategoryCard from "../components/CategoryCard";
import Helmet from "../components/Helmet";
import Topic from "../components/Topic";
import Category from "./Category";
import { useSelector, useDispatch } from "react-redux";
import PostSlice, {
	getAllTopicsByUserIDAsync,
} from "../features/topic/topicSlice";
import { useState, useEffect } from "react";

function Home({ blogger }) {
	const allTopics = useSelector((state) => state.topic);
	const user = useSelector((state) => state.user);
	const userID = blogger.id;
	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(getAllTopicsByUserIDAsync(userID));
	}, [dispatch, userID]);
	const allTopicElements = allTopics.map((topic) => {
		return (
			<Topic
				key={topic.id}
				id={topic.id}
				userID={topic.userID}
				topicName={topic.topicName}
				slug={topic.slug}
				blogs={topic.Blog}
				alias={blogger.alias}
			/>
		);
	});

	const isEqual = (...objects) =>
		objects.every((obj) => JSON.stringify(obj) === JSON.stringify(objects[0]));
	const isLoggedIn = isEqual(user, blogger);

	return (
		<div>
			<Helmet title="Trang chá»§">
				{
					<div className="home">
						<div className="home__new-blog">
							<div className="home__new-blog__pfp">
								<img src={blogger.profilepic} alt="" />
							</div>
							<div className="home__new-blog__placeholder"></div>
						</div>
						{isLoggedIn ? "" : ""}
						<div className="home__topics">{allTopicElements}</div>
					</div>
				}
			</Helmet>
		</div>
	);
}

export default Home;
