	//   const dispatch = useDispatch();
	//   useEffect(() => {
	//     dispatch(getAllBlogsByUserIDAsync(userID));
	//   }, [dispatch]);
	// //   const allPostsElements = allPosts.map((post) => {
	// //     return (
	// //     );
	// //   });
	return (
		<div>
			<Helmet title="Chủ đề">
				{
					
				}
				<AllCategoryCards />
			</Helmet>
		</div>
	);
}

export default Category;
